function fromBase64(value) {
    const base64 = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789-=';
    let result = 0;
    for (let i = 0; i < value.length; i++) {
        result = (result * 64 + base64.indexOf(value[i]));
    }
    return result;
}

function base64ToRecipeSegment(value) {
    return [fromBase64(value.slice(0, 4)), fromBase64(value.slice(4, 8))];
}

async function loadIndex() {
    const result = {};
    const resMeta = await fetch('data/metadata.json');
    const meta = await resMeta.json();

    for (let i = 0; i < meta.indexCount; i++) {
        const res = await fetch(`data/index/idx-${i + 1}.json`);
        const chunk = await res.json();
        for (const [id, emoji, name] of chunk) {
            result[id] = { id, emoji, name };
        }
    }
    return result;
}

async function findRecipeByNames(startName, endName) {
    const output = document.getElementById('output');
    const indexData = await loadIndex();

    // Ð—Ð½Ð°Ð¹Ñ‚Ð¸ ID Ð·Ð° Ñ–Ð¼'ÑÐ¼
    const startEntry = Object.values(indexData).find(x => x.name.toLowerCase() === startName.toLowerCase());
    const endEntry = Object.values(indexData).find(x => x.name.toLowerCase() === endName.toLowerCase());

    if (!startEntry || !endEntry) {
        return;
    }

    const a = startEntry.id;
    const b = endEntry.id;

    // Ð¨ÑƒÐºÐ°Ñ‚Ð¸ Ð¿Ð¾ Ð²ÑÑ–Ñ… Ñ‡Ð°Ð½ÐºÐ°Ñ…
    let found = false;
    for (let i = 1; i <= 78; i++) {
        try {
            const res = await fetch(`data/data/dat-${i}.json`);
            if (!res.ok) continue;

            const data = await res.json();

            for (const [id, rawTo, rawFrom] of data) {
                for (const encoded of rawFrom) {
                    const [x, y] = base64ToRecipeSegment(encoded);
                    if ((x === a && y === b) || (x === b && y === a)) {
                        const item = indexData[parseInt(id)];
                        return {
                            result: item.name,
                            emoji: item.emoji,
                            isNew: false
                        };
                    }
                }
            }
        } catch (_) {}
    }

}